ALTER TABLE `#__seminarman_courses` CHANGE `pdf_template` `invoice_template` INT( 11 ) UNSIGNED NOT NULL DEFAULT '0';
ALTER TABLE `#__seminarman_templates` CHANGE `pdf_template` `invoice_template` INT( 11 ) UNSIGNED NOT NULL DEFAULT '0';
ALTER TABLE `#__seminarman_courses` ADD `attlst_template` INT( 11 ) UNSIGNED NOT NULL DEFAULT '0' AFTER `invoice_template`;
ALTER TABLE `#__seminarman_templates` ADD `attlst_template` INT( 11 ) UNSIGNED NOT NULL DEFAULT '0' AFTER `invoice_template`;
ALTER TABLE `#__seminarman_pdftemplate` ADD `margin_bottom` double NOT NULL DEFAULT '0' AFTER `margin_top`;
