INSERT IGNORE INTO `#__seminarman_pdftemplate` (`templatefor`, `name`, `html`, `srcpdf`, `isdefault`, `margin_left`, `margin_right`, `margin_top`, `margin_bottom`, `paperformat`, `orientation`) VALUES 
(2, 'Zertifikatvorlage', '<p style="text-align: center;"><span style="font-size: x-large;"><strong>Zertifikat<br /></strong></span></p>\r\n<p> </p>\r\n<p style="text-align: center;">Hiermit wird bescheint, dass</p>\r\n<h4 style="text-align: center;"><span><strong>{SALUTATION} {FIRSTNAME} {LASTNAME}</strong></span></h4>\r\n<p style="text-align: center;">am Kurs</p>\r\n<h3 style="text-align: center;">{COURSE_TITLE}</h3>\r\n<p style="text-align: center;">vom {COURSE_START_DATE} bis {COURSE_FINISH_DATE}</p>\r\n<p style="text-align: center;">mit Erfolg teilgenommen hat.</p>\r\n<p style="text-align: center;"> </p>\r\n<p style="text-align: center;">Seminarinhalte:</p>\r\n<p style="text-align: center;">{COURSE_INTROTEXT}</p>\r\n<p style="text-align: center;">{COURSE_FULLTEXT}</p>\r\n<p> </p>\r\n<p style="text-align: right;">______________________________________________________</p>\r\n<p style="text-align: right;">{COURSE_FINISH_DATE}, {COURSE_LOCATION}</p>\r\n<p style="text-align: right;">{TUTOR}</p>', '', 1, 20, 20, 70, 20, 'A4', 'P');