INSERT IGNORE INTO `#__seminarman_emailtemplate` (`templatefor`, `title`, `subject`, `body`, `recipient`, `bcc`, `status`, `isdefault`) VALUES
(0, 'Buchungsbestätigung inkl Preisangabe mit Rabatt', 'Ihre Rechnung für die Kursbuchung "{COURSE_TITLE}"', '<p>Sehr geehrte(r) {SALUTATION} {TITLE}{LASTNAME},</p>\r\n<p>vielen Dank für Ihre Kursbuchung!<br />Mit dieser E-Mail erhalten Sie Ihre Rechnung im PDF-Format. Außerdem sind nochmal alle Details für Sie zusammengefasst.</p>\r\n<p>Rechnungsadresse:</p>\r\n<p>{CUSTOM_COMPANY}<br />{SALUTATION} {TITLE}{FIRSTNAME} {LASTNAME}<br />{CUSTOM_STREET}<br />{CUSTOM_ZIP} {CUSTOM_CITY}<br />{CUSTOM_COUNTRY}<br /><br />Tel: {CUSTOM_PHONE}</p>\r\n<p>Ihr gebuchter Kurs:</p>\r\n<p>Kursnr.: {COURSE_CODE}<br />Kurstitel: {COURSE_TITLE}<br />Datum: {COURSE_START_DATE} bis {COURSE_FINISH_DATE}<br />Veranstaltungsort: {COURSE_LOCATION}<br />Trainer: {TUTOR_SALUTATION} {TUTOR_OTHER_TITLE} {TUTOR_FIRSTNAME} {TUTOR_LASTNAME}</p>\r\n<p>Ihr Buchungspreis (pro Teilnehmer): {PRICE_REAL_BOOKING_SINGLE} zzgl. {PRICE_VAT_PERCENT}% MwSt, <br />Ihr Buchungspreis gesamt: {PRICE_REAL_BOOKING_TOTAL} zzgl. {PRICE_VAT_PERCENT}% MwSt, <br /><br />Preis gesamt inkl. {PRICE_VAT_PERCENT}% MwSt: {PRICE_TOTAL_VAT} Euro</p>', '{EMAIL}', '{ADMIN_CUSTOM_RECIPIENT}', NULL, 0);
INSERT IGNORE INTO `#__seminarman_pdftemplate` (`templatefor`, `name`, `html`, `srcpdf`, `isdefault`, `margin_left`, `margin_right`, `margin_top`, `margin_bottom`, `paperformat`, `orientation`) VALUES
(0, 'Rechnungsvorlage mit Rabatt', '<p>{CUSTOM_COMPANY}<br />{TITLE}{FIRSTNAME} {LASTNAME}<br /> {CUSTOM_STREET}<br /> {CUSTOM_ZIP} {CUSTOM_CITY}</p>\r\n<p> </p>\r\n<p> </p>\r\n<p style="text-align: right;"><strong>Datum</strong> {INVOICE_DATE}</p>\r\n<p> </p>\r\n<p><span style="font-size: x-large;"><strong>Ihre Rechnung {INVOICE_NUMBER}</strong></span></p>\r\n<p> </p>\r\n<table style="width: 100%; font-size: small;" border="1" cellpadding="5" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="width: 10%; text-align: left;">Pos.</td>\r\n<td style="width: 10%; text-align: left;">Menge</td>\r\n<td style="width: 50%; text-align: left;">Text</td>\r\n<td style="width: 15%; text-align: right;">Einzelpreis EUR</td>\r\n<td style="width: 15%; text-align: right;">Gesamtpreis EUR</td>\r\n</tr>\r\n<tr>\r\n<td style="text-align: left;">1</td>\r\n<td style="text-align: left;">{ATTENDEES}</td>\r\n<td style="text-align: left;">{COURSE_TITLE}<br />Kursnummer: {COURSE_CODE}<br />Vom {COURSE_START_DATE} bis {COURSE_FINISH_DATE} in {COURSE_LOCATION}</td>\r\n<td style="text-align: right;">{PRICE_PER_ATTENDEE}</td>\r\n<td style="text-align: right;">{PRICE_TOTAL}</td>\r\n</tr>\r\n<tr>\r\n<td style="text-align: right;" colspan="3">Gesamt Netto</td>\r\n<td style="text-align: right;" colspan="2">{PRICE_TOTAL}</td>\r\n</tr>\r\n<tr>\r\n<td style="text-align: right;" colspan="3">abzüglich Rabatt</td>\r\n<td style="text-align: right;" colspan="2">-{PRICE_TOTAL_DISCOUNT}</td>\r\n</tr>\r\n<tr>\r\n<td style="text-align: right;" colspan="3">Buchungspreis pro Teilnehmer</td>\r\n<td style="text-align: right;" colspan="2">{PRICE_REAL_BOOKING_SINGLE}</td>\r\n</tr>\r\n<tr>\r\n<td style="text-align: right;" colspan="3">Gesamter Buchungspreis</td>\r\n<td style="text-align: right;" colspan="2">{PRICE_REAL_BOOKING_TOTAL}</td>\r\n</tr>\r\n<tr>\r\n<td style="text-align: right;" colspan="3">zzgl. {PRICE_VAT_PERCENT}% MwSt.</td>\r\n<td style="text-align: right;" colspan="2">{PRICE_VAT}</td>\r\n</tr>\r\n<tr>\r\n<td style="text-align: right;" colspan="3">\r\n</td>\r\n<td style="text-align: right;" colspan="2">\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="text-align: right;" colspan="3"><strong>Gesamtbetrag</strong></td>\r\n<td style="text-align: right;" colspan="2"><strong>{PRICE_TOTAL_VAT}</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<p>Der Betrag muss 3 Werktage vor Kursbeginn auf unser Konto eingegangen sein.</p>\r\n<p></p>', '', 0, 20, 20, 70, 20, 'A4', 'P');